ent compile();

oop variables = 0; // list of variables
oop newVariables()
{
	GC_PUSH(oop, list, newObject(Variables));
	list->Variables.size = 0;
	list->Variables.capacity = 4;
	list->Variables.elements = (oop*)gc_beAtomic(malloc(sizeof(oop) * list->Variables.capacity));
	gc_popRoots(1);
	return list;
}

oop insertVariable(oop list, oop sym)
{
	// linear search for existing variable
	int nvariables = list ? list->Variables.size : 0;
	oop *variables = list ? list->Variables.elements : 0;
	for (int i = 0;  i < nvariables;  ++i){
		if ((variables[i]->Pair.a)== sym) return variables[i];
	}
	gc_pushRoot((void*)&list);
	gc_pushRoot((void*)&sym);
	list->Variables.elements = realloc(list->Variables.elements, sizeof(*list->Variables.elements) * (list->Variables.size + 1));
	list->Variables.elements[list->Variables.size] = newPair(sym, newInteger(list->Variables.size));
	gc_popRoots(2);
	return list->Variables.elements[list->Variables.size++];
}

oop searchVariable(oop list, oop sym)
{
	if (list == 0) return NULL; // no variables
	int nvariables = list->Variables.size;
	oop *variables = list->Variables.elements;
	if (nvariables == 0) return NULL; // no variables
	// linear search for existing variable
	for (int i = 0;  i < nvariables;  ++i)
	if ((variables[i]->Pair.a) == sym) return variables[i];
	return NULL;
}

#define discardVarialbes(V,X) V->Variables.size = X 

/* ==== STATE ==== */

/* =============== */

// STATESSm
// s1: pos, name;
// s2: pos, name;
// s3: pos, name;
enum { APSTATE = 0, ATRANSITION = 1 };

oop *states = 0;
int nstates = 0;
oop *transitions = 0; // transitions between states
int ntransitions = 0;

oop appendS0T1(oop name, int pos,int type)
{
	if(type != 0 && type != 1) {
		printf("type must be 0 or 1, got %d\n", type);
		exit(1);
	}
	oop *lists = type == APSTATE ? states : transitions;
	int *listSize = type == APSTATE ? &nstates : &ntransitions;
	for (int i = 0;  i < *listSize;  ++i) {
		oop state = lists[i];
		oop stateName = get(state, Pair,a);
		if (stateName == name) {
			printf("state %s already exists\n", get(name, Symbol,name));
			return state;
		}
	}
	lists = realloc(lists, sizeof(oop) * (*listSize + 1));
	gc_pushRoot((void*)&lists);
	gc_pushRoot((void*)&name);
	oop newState = newPair(name, newInteger(pos));
	gc_popRoots(2);
	lists[*listSize] = newState;
	(*listSize)++;
	return newState;
}

void setTransPos(ent prog){
	int *code = prog->IntArray.elements;
	for (int i = 0;  i < ntransitions;  ++i) {
		oop trans = transitions[i];
		oop transName = get(trans, Pair,a);
		int transPos = Integer_value(get(trans, Pair,b));
		for (int j = 0;  j < nstates;  ++j) {
			oop state = states[j];
			oop stateName = get(state, Pair,a);
			if (stateName == transName) {
				int statePos = Integer_value(get(state, Pair,b));
				code[transPos] = statePos;
			}
		}
	}
}


